// src/pages/Main.js

import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/pages/_main.scss';

const Main = () => {
  // useNavigate hook to programmatically navigate between routes
  const navigate = useNavigate();

  // Function to handle login button click and navigate to the login page
  const handleLoginClick = () => {
    navigate('/login');
  };

  return (
    <div className="first-page"> {/* Main container with background and content overlay */}
      <div className="overlay"> {/* Semi-transparent overlay to enhance text visibility */}
          <nav class="navbar navbar-expand-lg bg-body-tertiary navbar-main"> {/* Navbar section */}
            <div class="container-fluid navbar-box d-flex">

              {/* Navbar brand/logo */}
              <a onClick={handleLogoClick} class="navbar-brand" href="Logo">
                <img src='./images/logo.png' alt='logo'/>
              </a>

              {/* Navbar toggle button for mobile view */}
              <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span class="navbar-toggler-icon"></span>
              </button>

              {/* Navbar links and right-side buttons */}
              <div class="collapse navbar-collapse nav-center" id="navbarSupportedContent">
                <ul class="navbar-nav m-auto mb-2 mb-lg-0">
                  {/* Navbar links for each section */}
                  <li class="nav-item">
                    <a class="nav-link active" aria-current="page" href="Home">Home</a>
                  </li>
                  <li class="nav-item">
                    <a class="nav-link active" aria-current="page" href="Product">Product</a>
                  </li>
                  <li class="nav-item">
                    <a class="nav-link active" aria-current="page" href="Shop">Shop</a>
                  </li>
                  <li class="nav-item">
                    <a class="nav-link active" aria-current="page" href="About Us">About Us</a>
                  </li>
                  <li class="nav-item">
                    <a class="nav-link active" aria-current="page" href="Service">Service</a>
                  </li>
                  <li class="nav-item">
                    <a class="nav-link active" aria-current="page" href="Blog">Blog</a>
                  </li>
                </ul>

                {/* Right-side buttons for shopping and user login */}
                <div className="nav-right">
                  <button className="shop-button">
                    <img src='./icons/shop.svg' alt='shop' /> {/* Shop icon */}
                  </button>
                  <button onClick={handleLoginClick} className="login-button">
                    <img src='./icons/user.svg' alt='user' /> {/* User icon with login click handler */}
                  </button>
                </div>
              </div>

            </div>
          </nav>
        </div>

        {/* Welcome text section */}
        <div className='welcome-text'>
          <h2>Welcome to</h2> {/* Subtitle */}
          <h1>Plantopia</h1> {/* Main title */}
        </div>
    </div>
  );
};

export default Main; // Export the Main component
